package model;public class ComplexNumber {    private static final double epsilon = 0.0000001;    private final double real;    private final double imaginary;    public ComplexNumber(double real, double imaginary) {        this.real = real;        this.imaginary = imaginary;    }    public double getReal() {        return real;    }    public double getImaginary() {        return imaginary;    }    public static double getEpsilon() {        return epsilon;    }    @Override    public String toString() {        return "(" + real +                (imaginary >= 0 ? " + " : " - ") +                Math.abs(imaginary) + "*i)";    }}